import org.apache.commons.io.IOUtils
import java.nio.charset.*

def flowFile = session.get();
if (flowFile == null) {
    return;
}

def inJson = null

session.read(flowFile, { input ->
  def content = input.text
  inJson = new groovy.json.JsonSlurper().parseText(content)
})

inJson << flowFile.getAttributes();

flowFile = session.write(flowFile, {inputStream, out ->
    out.write(groovy.json.JsonOutput.toJson(inJson).getBytes());
} as StreamCallback)

session.transfer(flowFile, ExecuteScript.REL_SUCCESS)

